mapper.select:
select 
from platform_trade_detail


    and order_number = #{orderNumber,jdbcType=VARCHAR}


    and transaction_serial_number = #{transactionSerialNumber,jdbcType=VARCHAR}


    and pay_time = #{payTime,jdbcType=TIMESTAMP}


    and trade_type = #{tradeType,jdbcType=INTEGER}


    and trade_status = #{tradeStatus,jdbcType=INTEGER}


    and order_amount = #{orderAmount,jdbcType=INTEGER}


    and actually_amount = #{actuallyAmount,jdbcType=INTEGER}


    and beautician_commission = #{beauticianCommission,jdbcType=INTEGER}


    and store_net_income = #{storeNetIncome,jdbcType=INTEGER}


    and settlement_amount = #{settlementAmount,jdbcType=INTEGER}


    and platform_commission = #{platformCommission,jdbcType=INTEGER}


    and pay_method = #{payMethod,jdbcType=INTEGER}


    and pay_rate = #{payRate,jdbcType=INTEGER}


    and settlement_time = #{settlementTime,jdbcType=TIMESTAMP}


    and store_id = #{storeId,jdbcType=INTEGER}


    and store_name = #{storeName,jdbcType=VARCHAR}


    and store_phone = #{storePhone,jdbcType=VARCHAR}


    and store_bboss = #{storeBboss,jdbcType=VARCHAR}


    and beautician_id = #{beauticianId,jdbcType=INTEGER}


    and beautician_name = #{beauticianName,jdbcType=VARCHAR}


    and beautician_phone = #{beauticianPhone,jdbcType=VARCHAR}


    and buyers_nickname = #{buyersNickname,jdbcType=VARCHAR}


    and buyers_phone = #{buyersPhone,jdbcType=VARCHAR}


    and service_name = #{serviceName,jdbcType=VARCHAR}


    and unit_price = #{unitPrice,jdbcType=INTEGER}


    and coupons = #{coupons,jdbcType=INTEGER}


    and membership_card_discount = #{membershipCardDiscount,jdbcType=INTEGER}


    and one_cartoon_preferential = #{oneCartoonPreferential,jdbcType=INTEGER}


    and membershi_card = #{membershiCard,jdbcType=VARCHAR}


    and card_content = #{cardContent,jdbcType=VARCHAR}


    and face_value = #{faceValue,jdbcType=INTEGER}


    and sell_card_channel_reward = #{sellCardChannelReward,jdbcType=INTEGER}


    and total_price = #{totalPrice,jdbcType=INTEGER}


    and statistics_year = #{statisticsYear,jdbcType=VARCHAR}


    and statistics_year_month = #{statisticsYearMonth,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}


    and remarks = #{remarks,jdbcType=VARCHAR}


ORDER BY create_time DESC
