SELECT course.id courseId, course.course_name courseName, DATE_FORMAT(ref.create_time,'%Y-%m-%d %H:%m:%s') traininDate, DATE_FORMAT( ref.create_time, '%Y' ) yearStr FROM course course, course_beautician_ref ref WHERE course.is_enabled = TRUE AND ref.is_enabled = TRUE AND ref.course_id = course.id
AND ref.beautician_id = #{beauticianId,jdbcType=INTEGER}
AND ref.learning_status = #{learningStatus,jdbcType=TINYINT}
AND ref.sign_status = #{signStatus,jdbcType=TINYINT}
order by ref.create_time desc
