mapper.select:
select  from article


    AND article_type = #{articleType,jdbcType=TINYINT}


    AND mtitle = #{mtitle,jdbcType=VARCHAR}


    AND sub_heading = #{subHeading,jdbcType=VARCHAR}


    AND icon = #{icon,jdbcType=VARCHAR}


    AND main_img = #{mainImg,jdbcType=VARCHAR}


    AND article_tag = #{articleTag,jdbcType=VARCHAR}


    AND top_status = #{topStatus,jdbcType=TINYINT}


    AND weight_top = #{weightTop,jdbcType=INTEGER}


    AND release_status = #{releaseStatus,jdbcType=INTEGER}


    AND article_detail = #{articleDetail,jdbcType=LONGVARCHAR}


    AND create_time = #{createTime,jdbcType=TIMESTAMP}


    AND update_time = #{updateTime,jdbcType=TIMESTAMP}


    AND release_time = #{releaseTime,jdbcType=TIMESTAMP}


ORDER BY top_status DESC,weight_top DESC,create_time DESC
