mapper.select:
select

from freight_template


        AND template_code = #{templateCode,jdbcType=VARCHAR}


        AND template_name = #{templateName,jdbcType=VARCHAR}


        AND transport_method = #{transportMethod,jdbcType=TINYINT}


        AND pricing_method = #{pricingMethod,jdbcType=TINYINT}


        AND is_enabled = #{isEnabled,jdbcType=BIT}


        AND create_time = #{createTime,jdbcType=TIMESTAMP}


        AND update_time = #{updateTime,jdbcType=TIMESTAMP}

select
,
ftc.first_number AS firstNumber, ftc.first_price AS firstPrice, ftc.continued_number AS continuedNumber, ftc.continued_price AS continuedPrice
from freight_template ft, freight_template_city ftc
where ft.id = ftc.template_id
and ft.is_enabled = true
and ftc.is_enabled = true
and ftc.is_default = true

    AND ft.template_code = #{templateCode,jdbcType=VARCHAR}


    AND ft.template_name = #{templateName,jdbcType=VARCHAR}


    AND ft.transport_method = #{transportMethod,jdbcType=TINYINT}


    AND ft.pricing_method = #{pricingMethod,jdbcType=TINYINT}


    AND ft.is_enabled = #{isEnabled,jdbcType=BIT}


    AND ft.create_time = #{createTime,jdbcType=TIMESTAMP}


    AND ft.update_time = #{updateTime,jdbcType=TIMESTAMP}

select
count(*)
from freight_template
where is_enabled = true

    AND id != #{notId}


    AND template_name = #{templateName}
